const navMenu=document.getElementById("menu-btn"),showNav=document.getElementById("show-nav"),topHeader=document.getElementById("main-header"),body=document.getElementById("main");navMenu.addEventListener("click",(function(){showNav.classList.toggle("hidden"),topHeader.classList.toggle("header-shadow"),navMenu.classList.toggle("active"),body.classList.toggle("menu-is-open")})),window.addEventListener("scroll",(function(){topHeader.classList.toggle("header-shadow",window.scrollY>0)}));const accordions=document.getElementsByClassName("accordion-item");for(const e of accordions)e.addEventListener("click",(function(){e.classList.toggle("open")}));